import React, {useEffect, useState} from 'react';
import './style_projects.css';

import ProjectResume from './projectDescription';

function Project(props){
    const [state, setState] = useState({numOfProjects: 0});
    var columns = 3;

    const goHome = () => {
        props.history.push("/");
    }

    const goUserdashboard = () => {
        props.history.push("/dashboard");
    }

    const loadProjects = () => {
        // let display = document.querySelector(".displayProjects");
        let children = []
        for(let i = 0; i < (Math.pow(columns, 2)); i++){
            children.push(<ProjectResume id={state.numOfProjects} />);
            setState({numOfProjects: state.numOfProjects + 1});
        }
        return children;
    }

    const t = () => {
        document.getElementById("displayPro1").style.gridTemplateColumns = `repeat(${columns}, 1fr)`;
    }

    useEffect(() => {t()});

    return(
        <div className="divMainProjetcs">
            <header className="headerProjects">
                <h1 onClick={goHome}>ThinkR</h1>
                <div className="divSearch">
                    <input type="text" placeholder="nome do projeto"/>
                    <i class="fas fa-search"></i>
                </div>
                <p onClick={goUserdashboard}>User name</p>
            </header>
            <div className="mainProjetcs">
                <div className="displayProjects" id="displayPro1">
                    {loadProjects()}
                </div>
            </div>
        </div>
    )
}

export default Project;
