import React, { useState } from 'react';

function FormLogin(props) {

    const [state, setState] = useState({email: "email", password: "senha"});

    const updateState = (event) => {
        setState({email: event.target.value, password: "senha"});
        event.target.value = state.email;
    }

    const handleClickEntra = () => {
        props.history.push("/dashboard");
    }

    return(
        <form className="form" method="post">
            <h2>Entrar</h2>
            <div className="divInputForm">
                <input type="text" name="email" placeholder="email" value={state.email} onChange={updateState}/>
                <input type="password" name="senha" placeholder="senha" />
                <button onClick={handleClickEntra}>Entrar</button>
            </div>
            <p>Esqueci minha senha.</p>
        </form>
    );
}

export default FormLogin;
