import React, {useState} from 'react';
import './style_createProject.css';

function CreateProject(props){
    const [state, setState] = useState({textAreaHeight: "50vh"});
    const checkScrollBar = () => {
        const elem = document.getElementById("tA");
        if(elem.clientHeight < elem.scrollHeight){
            elem.style.height = "60vh";
        }
    }

    return(
        <div className="mainDivCreateProject">
            <div className="mainPage">
                <input type="text" placeholder="Titulo" className="inputTitulo"/>
                <p>Por: User name</p>
                <section>
                    <textarea type="text" placeholder="Escreva aqui" className="inputParagraph" id="tA" onChange={checkScrollBar}/>
                </section>
            </div>
        </div>
    )
}

export default CreateProject;
